import React from 'react';
import AgoraRTC from 'agora-rtc-sdk-ng';
import { AudienceLatencyLevelType } from 'agora-rtc-sdk-ng';



const CameraPage = () => {

    let rtc = {
        localAudioTrack: null,
        localVideoTrack: null,
        client: null,
    };

    let APP_ID = 'YOUR_APP_ID';
    let TOKEN = 'YOUR_TOKEN';
    let CHANNEL = 'YOUR_CHANNEL';
    let UID = 'YOUR_UID';

    const handleStartMonitoring = () => {
        // Logic to start monitoring
    };

    const handleJoinAsHost = async () => {
        try {
            rtc.client.setClientRole('host');
            await rtc.client.join(APP_ID, CHANNEL, TOKEN, UID);

            rtc.localAudioTrack = await AgoraRTC.createMicrophoneAudioTrack();
            rtc.localVideoTrack = await AgoraRTC.createCameraVideoTrack();

            await rtc.client.publish([rtc.localAudioTrack, rtc.localVideoTrack]);

            const localPlayerContainer = document.createElement('div');
            localPlayerContainer.id = UID;
            localPlayerContainer.textContent = 'Local user ' + UID;
            localPlayerContainer.style.width = '640px';
            localPlayerContainer.style.height = '480px';
            document.body.append(localPlayerContainer);

            rtc.localVideoTrack.play(localPlayerContainer);

            console.log('Host joined successfully!');
        } catch (error) {
            console.error('Failed to join as host:', error);
        }
    };

    return (
        <div>
            <h1>Camera Page</h1>
            <button onClick={handleStartMonitoring}>Start Monitoring</button>
            <button onClick={handleJoinAsHost}>Join as Host</button>
        </div>
    );
};

export default CameraPage;
